import { StatusBar } from 'expo-status-bar';
import React from 'react';
import { SafeAreaView, View, FlatList, StyleSheet, Text, ActivityIndicator, Button } from 'react-native';
import { RepositoryModel } from '../model/repository';




const RepositoryLine = ({ repositories, fetchMore, isLoading }: { repositories: RepositoryModel[], fetchMore: Function, isLoading: boolean }) => {
    const renderItem = ({ item }: { item: RepositoryModel }) => (
        <View style={styles.item}>
            <View style={styles.firstLine}>
                <Text style={styles.title} numberOfLines={1}>{item.owner.login} / {item.name}</Text>
                <Text style={styles.stars} >{item.stargazers_count}</Text>
            </View>
            {item.description && < Text style={styles.description} numberOfLines={2}>{item.description}</Text>}
            {item.language && <Text style={styles.language} >{item.language}</Text>}
        </View >
    );

    const footer = () => {
        return isLoading || true ? <ActivityIndicator style={{ alignSelf: "center" }} size="large" color="#608BB3" /> : <View style={styles.item}></View>
    }

    return (
        <SafeAreaView style={styles.container}>
            <FlatList
                contentContainerStyle={{ flex: 1, alignItems: 'center' }}
                data={repositories}
                renderItem={renderItem}
                ListFooterComponent={footer}
                ListFooterComponentStyle={styles.loader}
                keyExtractor={item => item.id + ''}
                onEndReachedThreshold={0.01}
                onEndReached={info => {
                    fetchMore(info);
                }}
            />
        </SafeAreaView>
    );
}

const styles = StyleSheet.create({
    container: {
        display: "flex",
        flex: 1,
    },
    item: {
        padding: 8,
        marginHorizontal: 16,
        borderBottomColor: "#E6E6E6",
        borderBottomWidth: 1,
        fontSize: 14,
    },
    stars: {
    },
    title: {
        color: "#608BB3",
    },
    description: {
        color: "#4C4C58",
        fontSize: 12,
        marginBottom: 8,
    },
    language: {
        color: "#4C4C58",
        fontSize: 12,
        fontWeight: 'bold'
    },
    firstLine: {
        display: "flex",
        justifyContent: "space-between",
        marginBottom: 8,
        flexDirection: "row",
        alignItems: "center"
    },
    swipe: {
        flex: 1,
        backgroundColor: "#aaa"
    },
    loader: {
        display: "flex",
        backgroundColor: "#a5b2a1",
        flex: 1,
        flexGrow: 1,
        alignItems: "stretch",
        alignContent: "center",
        flexDirection: "column",
        borderWidth: 3,
        borderColor: "#23bca5",
        alignSelf: 'stretch',
    }
});


export default RepositoryLine;
