import { StatusBar } from 'expo-status-bar';
import React, { useState } from 'react';
import { StyleSheet, Image, View, Button } from 'react-native';
import { RepositoryModel } from '../model/repository';

interface Response {
    incomplete_results: boolean;
    items: RepositoryModel[];
    total_count: number;
}


const RepositoryView = () => {

    const [fromFetch, setFromFetch] = useState(false);
    const [loading, setLoading] = useState(false);
    const [dataSource, setDataSource] = useState(null);

    const getRepos = () => {
        setFromFetch(true);
        setLoading(false);
        const lastMonth = new Date(new Date().setMonth(new Date().getMonth() - 1))
            .toISOString()
            .split('T')[0];

        fetch('https://api.github.com/search/repositories?q=created:>' + lastMonth + '&sort=stars&order=desc')
            .then(response => response.json())
            .then((responseJson: Response) => {
                console.log('getting data from fetch', responseJson)
                const res: RepositoryModel[] = responseJson.items.map(({
                    name,
                    description,
                    html_url,
                    created_at,
                    stargazers_count,
                    open_issues,
                    owner
                }) => ({
                    name,
                    description,
                    html_url,
                    created_at: new Date(created_at),
                    stargazers_count,
                    open_issues,
                    owner: {
                        avatar_url: owner.avatar_url,
                        login: owner.login,
                        html_url: owner.html_url
                    }
                }));
                console.log();

                setLoading(false);
                //setDataSource(responseJson);
            })
            .catch(error => console.log(error))
    }

    return (
        <View style={styles.container}>
            <Button title={loading ? "loading" : "Load"} onPress={getRepos} />
            <StatusBar style="auto" />
        </View>
    );
}


const styles = StyleSheet.create({
    container: {
        flex: 1,
        color: '#9a9ec9',
        backgroundColor: '#4b56a4',
        alignItems: 'center',
        justifyContent: 'center',
    },
});

export default RepositoryView;
