import { StatusBar } from 'expo-status-bar';
import React from 'react';
import { SafeAreaView, View, FlatList, StyleSheet, Text, Button, ActivityIndicator } from 'react-native';
import { RepositoryModel } from '../model/repository';




const RepositoryLine = ({ repositories, fetchMore, isLoading }: { repositories: RepositoryModel[], fetchMore: Function, isLoading: boolean }) => {

    const renderItem = ({ item, index }: { item: RepositoryModel, index: number },) => (
        <View style={styles.item}>
            <Text style={styles.title}>{item.owner.login} / {item.name}</Text>
            <Text style={styles.description} >{item.description}</Text>
            {item.language && <Text style={styles.language} >{item.language}</Text>}
        </View >
    );

    const footer = () => {
        return isLoading ? <ActivityIndicator size="large" color="#355070" /> : <View style={styles.item}><Button title={"More"} onPress={() => fetchMore()}></Button></View>
    }

    return (
        <SafeAreaView style={styles.container}>
            <FlatList
                data={repositories}
                renderItem={renderItem}
                ListFooterComponent={footer}
                keyExtractor={item => item.id + ''}
                onEndReachedThreshold={0.01}
                onEndReached={info => {
                    fetchMore(info);
                }}
            />
        </SafeAreaView>
    );
}

const styles = StyleSheet.create({
    container: {
        flex: 1,
    },
    item: {

        padding: 8,
        marginHorizontal: 8,
    },
    title: {
        color: "#608BB3",
        fontSize: 14,
    },
    description: {
        fontSize: 12,
    },
    language: {
        fontWeight: 'bold'
    }
});

export default RepositoryLine;
