import { StatusBar } from 'expo-status-bar';
import React from 'react';
import { SafeAreaView, View, FlatList, StyleSheet, Text, ActivityIndicator, Button, Pressable } from 'react-native';
import { RepositoryModel } from '../model/repository';
import Icon from 'react-native-vector-icons/MaterialIcons';
import SwipeableFlatList from 'react-native-swipeable-list';

const RepositoryLine = ({ repositories, fetchMore, isLoading }: { repositories: RepositoryModel[], fetchMore: Function, isLoading: boolean }) => {


    const renderItem = ({ item }: { item: RepositoryModel }) => {
        const leftContent = <View><Icon name="star" size={14} color={"#4C4C58"} /></View>
        return (
            <View style={styles.item}>
                <View style={styles.firstLine}>
                    <Text style={styles.title} numberOfLines={1}>{item.owner.login} / {item.name} </Text>
                    <View style={styles.stars}>
                        <Icon name="star" size={14} color={"#4C4C58"} />
                        <Text style={{ marginLeft: 4 }} >{item.stargazers_count}</Text>
                    </View>
                </View>
                {item.description && < Text style={styles.description} numberOfLines={2}>{item.description}</Text>}
                {/*item.language && <Text style={styles.language} >{item.language}</Text>*/}
            </View >
        )
    }
        ;

    const footer = () => {
        return isLoading ? <ActivityIndicator style={{ flexGrow: 1 }} size="large" color="#608BB3" /> : <View style={styles.item}></View>
    }

    const QuickActions = (index, qaItem) => {
        return (
            <View style={styles.qaContainer}>
                <View style={[styles.button, styles.button1]}>
                    <Pressable onPress={() => archiveItem(qaItem.id)}>
                        <Text style={[styles.buttonText, styles.button1Text]}>Archive</Text>
                    </Pressable>
                </View>
            </View>
        );
    };

    return (
        <SafeAreaView style={styles.container}>
            <SwipeableFlatList
                contentContainerStyle={{ flexGrow: 1 }}
                maxSwipeDistance={240}
                data={repositories}
                renderItem={renderItem}
                ListFooterComponent={footer}
                ListFooterComponentStyle={styles.loader}
                renderQuickActions={({ index, item }) => QuickActions(index, item)}
                keyExtractor={item => item.id + ''}
                onEndReachedThreshold={0.01}
                onEndReached={info => {
                    fetchMore(info);
                }}
            />
        </SafeAreaView>
    );
}

const styles = StyleSheet.create({
    container: {
        display: "flex",
        flex: 1,
        width: "100%",
    },
    item: {
        padding: 8,
        marginHorizontal: 16,
        borderBottomColor: "#E6E6E6",
        borderBottomWidth: 1,
        fontSize: 14,
        color: "#4C4C58",
    },
    stars: {
        display: "flex",
        flexDirection: "row",
        alignItems: "center",
        marginLeft: 8,
    },
    title: {
        color: "#608BB3",
        flexShrink: 1,
    },
    description: {
        color: "#4C4C58",
        fontSize: 12,
        marginBottom: 8,
    },
    language: {
        color: "#4C4C58",
        fontSize: 12,
        fontWeight: 'bold'
    },
    firstLine: {
        display: "flex",
        justifyContent: "space-between",
        marginBottom: 8,
        flexDirection: "row",
        alignItems: "center",
        flexGrow: 1,
        position: "relative"
    },
    swipe: {
        flex: 1,
        backgroundColor: "#aaa"
    },
    loader: {
        display: "flex",
        flexGrow: 1,
        alignContent: "center",
        marginTop: 16,
        marginBottom: 16,

    },
    qaContainer: {
        flex: 1,
        flexDirection: 'row',
        justifyContent: 'flex-end',
    },
    button: {
        width: 80,
        alignItems: 'center',
        justifyContent: 'center',
    },
});


export default RepositoryLine;
