import React from 'react';
import { StyleSheet, Image, View, Button, StatusBar } from 'react-native';
import AsyncStorage from '@react-native-community/async-storage';

const Home = () => {

    const clearAppData = async function () {
        try {
            const keys = await AsyncStorage.getAllKeys();
            console.log("Keys", keys);
            await AsyncStorage.multiRemove(keys);
        } catch (error) {
            console.error('Error clearing app data.');
        }
    }

    return (
        <View style={styles.container}>
            <View
                style={{
                    backgroundColor: '#00BCD4',
                    height: Platform.OS === 'ios' ? 20 : StatusBar.currentHeight,
                }}>
                <StatusBar
                    backgroundColor="#b3e6ff"
                    barStyle="dark-content"
                    hidden={false}
                    translucent={false}
                /></View>
            <Image source={require('../img/GitHub_Logo_White.png')} style={{ width: 90, height: 40 }} />
            <Button title={"clearStorage"} onPress={clearAppData}></Button>

        </View>
    );
}

const styles = StyleSheet.create({
    container: {
        flex: 1,
        color: '#9a9ec9',
        backgroundColor: '#4b56a4',
        alignItems: 'center',
        justifyContent: 'center',
    },
});

export default Home;
