import { StatusBar } from 'expo-status-bar';
import React, { useState } from 'react';
import { StyleSheet, Image, View, Button } from 'react-native';
import { Repos } from '../model/repos';

interface ResponseFetch {
    incomplete_results: boolean;
    items: Repos[];
    total_count: number;
}


const RepositoryView = () => {

    const [loading, setLoading] = useState(false);
    const [dataSource, setDataSource] = useState(null);

    const getRepos = () => {
        setLoading(false);
        const lastMonth = new Date(new Date().setMonth(new Date().getMonth() - 1))
            .toISOString()
            .split('T')[0];

        fetch('https://api.github.com/search/repositories?q=created:>' + lastMonth + '&sort=stars&order=desc')
            .then((response) => {
                console.log('getting data from fetch', response);
                // response.items.map((response: any) => { console.log(response); });
                setLoading(false);
                //setDataSource(responseJson)
            })
            .catch(error => console.log(error))
    }

    return (
        <View style={styles.container}>
            <Button title={loading ? "loading" : "Load"} onPress={getRepos} />
            <StatusBar style="auto" />
        </View>
    );
}


const styles = StyleSheet.create({
    container: {
        flex: 1,
        color: '#9a9ec9',
        backgroundColor: '#4b56a4',
        alignItems: 'center',
        justifyContent: 'center',
    },
});

export default RepositoryView;
